    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        
        Email = email.getText();
        Senha = senha.getText();
        Nome = name.getText();
        Cpf = CPFtext.getText();
        Data = date.getText();
        Telefone = tel.getText();
        
                    
        if(Email.equals("") || Senha.equals("") || Nome.equals("") || Data.equals("")  || Telefone.equals("")){
            JOptionPane.showMessageDialog (null, "Campo n√£o preeenchido, Tente novamente");
        }
        else{
            JOptionPane.showMessageDialog (null,"Cadastro concluido com sucesso!");
            dispose();
            alo = 1;
        }
    }//GEN-LAST:event_jButton1ActionPerformed

    
    
